import{_ as a,I as s,c as r,o,ah as i,J as l}from"./chunks/framework.ckOWworG.js";const b=JSON.parse('{"title":"Usage Guide","description":"","frontmatter":{},"headers":[],"relativePath":"usage.md","filePath":"usage.md"}'),n={name:"usage.md"};function g(c,e,d,m,p,u){const t=s("ThemeImage");return o(),r("div",null,[e[0]||(e[0]=i('<h1 id="usage-guide" tabindex="-1">Usage Guide <a class="header-anchor" href="#usage-guide" aria-label="Permalink to “Usage Guide”">​</a></h1><p>This comprehensive guide walks you through using Kryvea for managing security assessments.</p><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to “Getting Started”">​</a></h2><h3 id="first-login" tabindex="-1">First Login <a class="header-anchor" href="#first-login" aria-label="Permalink to “First Login”">​</a></h3><ol><li>Navigate to <code>https://localhost</code> (or your configured domain)</li><li>Enter default credentials: <ul><li><strong>Username</strong>: <code>kryvea</code></li><li><strong>Password</strong>: <code>kryveapassword</code></li></ul></li><li>When prompted, enter a new password that meets the following requirements: <ul><li>At least 10 characters long</li><li>Includes at least one uppercase letter</li><li>Includes at least one lowercase letter</li><li>Includes at least one special symbol</li></ul></li></ol><h2 id="user-roles" tabindex="-1">User Roles <a class="header-anchor" href="#user-roles" aria-label="Permalink to “User Roles”">​</a></h2><p>Kryvea has two user roles:</p><h3 id="admin" tabindex="-1">Admin <a class="header-anchor" href="#admin" aria-label="Permalink to “Admin”">​</a></h3><ul><li>Full access to all features</li><li>Customer management (create, edit, delete)</li><li>User management</li><li>Category management</li><li>Global template management</li><li>System settings and logs</li></ul><h3 id="user" tabindex="-1">User <a class="header-anchor" href="#user" aria-label="Permalink to “User”">​</a></h3><ul><li>Assessment management</li><li>Vulnerability tracking</li><li>Report generation</li><li>Limited to assigned customers</li><li>Cannot manage users or global settings</li></ul><h2 id="workflow-overview" tabindex="-1">Workflow Overview <a class="header-anchor" href="#workflow-overview" aria-label="Permalink to “Workflow Overview”">​</a></h2><p>The typical Kryvea workflow:</p><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><ul><li>Create or import categories from <a href="https://github.com/Alexius22/kryvea/blob/main/app/data/generic_categories.json" target="_blank" rel="noreferrer">here</a></li><li>Upload a template report from <a href="https://github.com/Alexius22/kryvea/blob/main/app/data/report-template-example.docx" target="_blank" rel="noreferrer">here</a></li></ul></div><h3 id="normal-workflow" tabindex="-1">Normal workflow <a class="header-anchor" href="#normal-workflow" aria-label="Permalink to “Normal workflow”">​</a></h3><ol><li>Create Customer</li><li>Create Assessment</li><li>Add Vulnerabilities (Manual or Import)</li><li>Assign Categories &amp; CVSS Scores</li><li>Add PoC</li><li>Generate report (DOCX/XLSX)</li></ol><h2 id="customer-management" tabindex="-1">Customer Management <a class="header-anchor" href="#customer-management" aria-label="Permalink to “Customer Management”">​</a></h2><h3 id="creating-a-customer" tabindex="-1">Creating a Customer <a class="header-anchor" href="#creating-a-customer" aria-label="Permalink to “Creating a Customer”">​</a></h3><p><strong>Admin only</strong></p><ol><li>Navigate to <strong>Customers</strong> -&gt; <strong>New Customer</strong></li><li>Fill in customer details: <ul><li><strong>Company Name</strong>: Customer organization name</li><li><strong>Language</strong>: Default language that will be preselected in new assessment</li><li><strong>Logo</strong>: Upload customer logo (PNG, JPG) (Optional)</li></ul></li></ol><h3 id="uploading-custom-templates" tabindex="-1">Uploading Custom Templates <a class="header-anchor" href="#uploading-custom-templates" aria-label="Permalink to “Uploading Custom Templates”">​</a></h3><ol><li>Go to <strong>Edit Customer</strong> page</li><li>Insert a template name</li><li>Insert a type to help you to identify better the template (Optional)</li><li>Select a DOCX/XLSX template file</li><li>Click <strong>Upload</strong></li></ol><h3 id="managing-customers" tabindex="-1">Managing Customers <a class="header-anchor" href="#managing-customers" aria-label="Permalink to “Managing Customers”">​</a></h3><p><strong>View all customers:</strong></p><ul><li>Navigate to <strong>Customers</strong></li><li>Search by name</li><li>Click on a customer to view assessments associated</li></ul><p><strong>Edit customer:</strong></p><ul><li>Customer Detail -&gt; <strong>Edit</strong></li><li>Update name, language, or logo</li><li>Save changes</li></ul><p><strong>Delete customer:</strong></p><ul><li>Customer Detail -&gt; <strong>Delete</strong></li><li>⚠️ <strong>Warning</strong>: This will delete all associated assessments, targets and vulnerabilities</li></ul><h2 id="assessment-management" tabindex="-1">Assessment Management <a class="header-anchor" href="#assessment-management" aria-label="Permalink to “Assessment Management”">​</a></h2><h3 id="creating-an-assessment" tabindex="-1">Creating an Assessment <a class="header-anchor" href="#creating-an-assessment" aria-label="Permalink to “Creating an Assessment”">​</a></h3>',31)),l(t,{light:"/images/new_assessment.png",dark:"/images/new_assessment_dark.png",alt:"New Assessment"}),e[1]||(e[1]=i('<ol><li><p>Navigate to <strong>Customer Detail</strong> -&gt; <strong>Assessments</strong> -&gt; <strong>New Assessment</strong></p></li><li><p>Fill in assessment information:</p><p><strong>Basic Information:</strong></p><ul><li><strong>Name</strong>: Assessment name</li><li><strong>Assessment Type</strong>: Select assessment type (e.g., Web Application Penetration Test, Network Penetration Test, etc)</li><li><strong>Language</strong>: Select assessment language</li></ul><p><strong>Timeline:</strong></p><ul><li><strong>Start Date</strong>: Testing begins</li><li><strong>End Date</strong>: Testing completes</li><li><strong>Kickoff Date</strong>: Project start</li></ul><p><strong>Targets:</strong></p><ul><li>Add target systems/applications</li><li>Each target can have vulnerabilities associated</li></ul><p><strong>Configuration:</strong></p><ul><li><strong>CVSS Versions</strong>: Select which CVSS versions to use (v3.1, v4.0c)</li><li><strong>Environment</strong>: Production | Pre-Production | Testing (Optional)</li><li><strong>Testing Type</strong>: Black box | Grey box | White box (Optional)</li><li><strong>OSSTMM Vector</strong>: OSSTMM classification (Optional)</li></ul></li></ol><h3 id="managing-assessments" tabindex="-1">Managing Assessments <a class="header-anchor" href="#managing-assessments" aria-label="Permalink to “Managing Assessments”">​</a></h3><p><strong>View assessments:</strong></p><ul><li>Customer name -&gt; <strong>Assessments</strong> tab</li></ul><div class="tip custom-block"><p class="custom-block-title custom-block-title-default">TIP</p><p><strong>Take ownership</strong> ⭐: Once you take ownership of the assessment, it will become accessible on the Dashboard page.</p></div><p><strong>Edit assessment:</strong></p><ul><li>Assessment -&gt; <strong>Edit</strong></li><li>Update any field</li><li>Save changes</li></ul><p><strong>Clone assessment:</strong></p><ul><li>Assessment -&gt; <strong>Clone</strong></li><li>Optionally include PoCs</li><li>Creates duplicate with all vulnerabilities</li></ul><p><strong>Delete assessment:</strong></p><ul><li>Assessment -&gt; <strong>Delete</strong></li><li>⚠️ <strong>Warning</strong>: Deletes all associated vulnerabilities</li></ul><h3 id="assessment-status-workflow" tabindex="-1">Assessment Status Workflow <a class="header-anchor" href="#assessment-status-workflow" aria-label="Permalink to “Assessment Status Workflow”">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>On Hold -&gt; In Progress -&gt; Completed</span></span></code></pre></div><p>Update status from the assessments page.</p><h2 id="vulnerability-management" tabindex="-1">Vulnerability Management <a class="header-anchor" href="#vulnerability-management" aria-label="Permalink to “Vulnerability Management”">​</a></h2><h3 id="adding-vulnerabilities-manually" tabindex="-1">Adding Vulnerabilities Manually <a class="header-anchor" href="#adding-vulnerabilities-manually" aria-label="Permalink to “Adding Vulnerabilities Manually”">​</a></h3>',16)),l(t,{light:"/images/new_vulnerability.png",dark:"/images/new_vulnerability_dark.png",alt:"New Vulnerability"}),e[2]||(e[2]=i('<ol><li><p>Navigate to <strong>Assessment</strong> -&gt; <strong>Vulnerabilities</strong> -&gt; <strong>New Vulnerability</strong></p></li><li><p>Select a <strong>Category</strong> (e.g., &quot;SQL Injection&quot;, &quot;XSS&quot;)</p><ul><li>Generic description auto-populated from categories</li><li>Ticking the &quot;Show categories from all sources&quot; checkbox will include categories created during automatic scans (e.g. Nessus, Burp) among the selectable categories</li><li>Ticking the &quot;Generic remediation&quot; checkbox will add the remediation included in the category; leaving this box unticked will prevent it from being included</li></ul></li><li><p>Fill in details:</p><ul><li><strong>Detailed Title</strong>: Specific vulnerability name (Optional)</li><li><strong>Status</strong>: Open | Fixed | Partially Fixed</li><li><strong>Target</strong>: Affected target</li></ul></li><li><p><strong>CVSS Scoring</strong>:</p><ul><li>Select CVSS version (based on assessment config)</li><li>Use the calculator or enter vector string</li><li>Score auto-calculated</li></ul></li><li><p><strong>Description &amp; Remediation</strong>:</p><ul><li>Write details to specifically describe the identified vulnerability</li><li>If necessary, include a more specific remediation</li></ul></li><li><p><strong>References</strong>:</p><ul><li>Add URLs, CVE IDs, etc. (One per line)</li></ul></li></ol><h3 id="cvss-calculator" tabindex="-1">CVSS Calculator <a class="header-anchor" href="#cvss-calculator" aria-label="Permalink to “CVSS Calculator”">​</a></h3><p>The built-in CVSS calculator supports:</p><ul><li><strong>CVSS v3.1</strong>: Base, Temporal, Environmental</li><li><strong>CVSS v4</strong>: Base, Supplemental, Environmental, Threat metrics</li></ul><p><strong>Usage:</strong></p><ol><li>Select CVSS version</li><li>Choose values for each metric</li><li>Vector string and score auto-generate</li><li>Severity level displayed (None, Low, Medium, High, Critical)</li></ol><h3 id="adding-proof-of-concept-poc" tabindex="-1">Adding Proof of Concept (PoC) <a class="header-anchor" href="#adding-proof-of-concept-poc" aria-label="Permalink to “Adding Proof of Concept (PoC)”">​</a></h3>',7)),l(t,{light:"/images/edit_poc.png",dark:"/images/edit_poc_dark.png",alt:"Edit PoC"}),e[3]||(e[3]=i('<ol><li>Navigate to <strong>Vulnerability Detail</strong> -&gt; <strong>Edit PoC</strong> tab</li><li>Use the header bar to add: <ul><li>HTTP Request/Response</li><li>Image</li><li>Text</li></ul></li><li>Add or erase highlighting in the request/response and text sections to emphasise particular parts</li><li>Click <strong>Save</strong></li></ol><h3 id="searching-vulnerabilities" tabindex="-1">Searching Vulnerabilities <a class="header-anchor" href="#searching-vulnerabilities" aria-label="Permalink to “Searching Vulnerabilities”">​</a></h3>',2)),l(t,{light:"/images/vulnerability_search.png",dark:"/images/vulnerability_search_dark.png",alt:"Vulnerability Search"}),e[4]||(e[4]=i('<p><strong>Global search:</strong></p><ul><li>Navigate to <strong>Vulnerability Search</strong></li><li>Search across all assessments</li><li>Filter by: <ul><li>Assessment</li><li>User</li><li>Date range</li><li>Customer</li><li>CVSS Versions</li><li>CVSS score range</li></ul></li></ul><h3 id="copying-vulnerabilities" tabindex="-1">Copying Vulnerabilities <a class="header-anchor" href="#copying-vulnerabilities" aria-label="Permalink to “Copying Vulnerabilities”">​</a></h3><p>Copy vulnerabilities between assessments:</p><ol><li>Find vulnerability in search</li><li>Click <strong>Copy to</strong></li><li>Select destination: <ul><li>Customer</li><li>Assessment</li><li>Target</li></ul></li><li>Optionally include PoC</li><li>Click <strong>Copy</strong></li></ol><h2 id="category-management" tabindex="-1">Category Management <a class="header-anchor" href="#category-management" aria-label="Permalink to “Category Management”">​</a></h2><p><strong>Admin only</strong></p><p>Categories define vulnerability types using generic descriptions and remediation guidance, with support for multiple languages.</p><h3 id="browsing-categories" tabindex="-1">Browsing Categories <a class="header-anchor" href="#browsing-categories" aria-label="Permalink to “Browsing Categories”">​</a></h3><ul><li>Navigate to <strong>Categories</strong></li><li>View all available categories</li><li>It can be preloaded with some items from the OWASP Top 10, which can be found <a href="https://github.com/Alexius22/kryvea/blob/main/app/data/generic_categories.json" target="_blank" rel="noreferrer">here</a></li></ul><h3 id="creating-a-category" tabindex="-1">Creating a Category <a class="header-anchor" href="#creating-a-category" aria-label="Permalink to “Creating a Category”">​</a></h3><ol><li><p><strong>Categories</strong> -&gt; <strong>New Category</strong></p></li><li><p>Fill in details:</p><ul><li><strong>Identifier</strong>: Short code (e.g., &quot;A01:2021&quot;)</li><li><strong>Name</strong>: Vulnerability type (e.g., &quot;SQL Injection&quot;)</li><li><strong>Source</strong>: <ul><li>OWASP Top 10 Web</li><li>OWASP Top 10 Mobile</li><li>OWASP Top 10 API</li><li>OWASP Top 10 for LLM</li><li>ATT&amp;CK</li><li>Burp</li><li>CWE</li><li>Nessus</li></ul></li><li><strong>References</strong>: Links to CWE, OWASP, etc.</li></ul></li><li><p><strong>Generic Description</strong> and <strong>Generic Remediation</strong> (multilingual), it is set by default to the language selected in the <code>Settings</code>, but any language can be added.</p></li></ol><h3 id="importing-categories" tabindex="-1">Importing Categories <a class="header-anchor" href="#importing-categories" aria-label="Permalink to “Importing Categories”">​</a></h3><ol><li><strong>Categories</strong> -&gt; <strong>Import</strong></li><li>Select JSON file with category definitions (e.g., <code>app/data/generic_categories.json</code>)</li><li>Choose import mode: <ul><li><strong>Add new only</strong>: Skip existing</li><li><strong>Override</strong>: Update existing categories</li></ul></li><li>Click <strong>Import</strong></li></ol><h3 id="exporting-categories" tabindex="-1">Exporting Categories <a class="header-anchor" href="#exporting-categories" aria-label="Permalink to “Exporting Categories”">​</a></h3><ol><li><strong>Categories</strong> -&gt; <strong>Export</strong></li><li>Downloads JSON file with all categories</li><li>Share with team or backup</li></ol><h2 id="report-generation" tabindex="-1">Report Generation <a class="header-anchor" href="#report-generation" aria-label="Permalink to “Report Generation”">​</a></h2><h3 id="generating-docx-reports" tabindex="-1">Generating DOCX Reports <a class="header-anchor" href="#generating-docx-reports" aria-label="Permalink to “Generating DOCX Reports”">​</a></h3><ol><li>Navigate to <strong>Assessment Detail</strong></li><li>Click <strong>Export</strong> -&gt; <strong>DOCX</strong></li><li>Select template: <ul><li>Global template</li><li>Customer-specific template</li></ul></li><li>Configure options: <ul><li>Include informational findings</li></ul></li><li>Click <strong>Generate</strong></li><li>Download Word document</li></ol><h3 id="generating-xlsx-reports" tabindex="-1">Generating XLSX Reports <a class="header-anchor" href="#generating-xlsx-reports" aria-label="Permalink to “Generating XLSX Reports”">​</a></h3><ol><li>Navigate to <strong>Assessment Detail</strong></li><li>Click <strong>Export</strong> -&gt; <strong>XLSX</strong></li><li>Select template: <ul><li>Global template</li><li>Customer-specific template</li></ul></li><li>Configure options: <ul><li>Include informational findings</li></ul></li><li>Click <strong>Generate</strong></li><li>Download Excel document</li></ol><h3 id="generating-xlsx-reports-with-images-with-zip" tabindex="-1">Generating XLSX Reports with images with ZIP <a class="header-anchor" href="#generating-xlsx-reports-with-images-with-zip" aria-label="Permalink to “Generating XLSX Reports with images with ZIP”">​</a></h3><ol><li>Navigate to <strong>Assessment Detail</strong></li><li>Click <strong>Export</strong> -&gt; <strong>ZIP</strong></li><li>Configure options: <ul><li>Include informational findings</li></ul></li><li>Click <strong>Generate</strong></li><li>Download zip archive that contains a xlsx file and a folder with images</li></ol><h2 id="import-export" tabindex="-1">Import &amp; Export <a class="header-anchor" href="#import-export" aria-label="Permalink to “Import &amp; Export”">​</a></h2><h3 id="importing-from-burp-suite" tabindex="-1">Importing from Burp Suite <a class="header-anchor" href="#importing-from-burp-suite" aria-label="Permalink to “Importing from Burp Suite”">​</a></h3><ol><li><p>In Burp Suite:</p><ul><li>Scanner -&gt; Right-click -&gt; <strong>Save selected issues</strong></li><li>Choose XML format</li></ul></li><li><p>In Kryvea:</p><ul><li>Assessment -&gt; <strong>Import</strong> -&gt; <strong>Burp Suite</strong></li><li>Upload XML file</li><li>Map to targets</li><li>Vulnerabilities auto-created with CVSS scores</li></ul></li></ol><h3 id="importing-from-nessus" tabindex="-1">Importing from Nessus <a class="header-anchor" href="#importing-from-nessus" aria-label="Permalink to “Importing from Nessus”">​</a></h3><ol><li><p>In Nessus:</p><ul><li>Export scan results</li><li>Choose Nessus format (.nessus)</li></ul></li><li><p>In Kryvea:</p><ul><li>Assessment -&gt; <strong>Import</strong> -&gt; <strong>Nessus</strong></li><li>Upload .nessus file</li><li>Map plugins to categories</li><li>Vulnerabilities auto-created</li></ul></li></ol><h3 id="category-import-export" tabindex="-1">Category Import/Export <a class="header-anchor" href="#category-import-export" aria-label="Permalink to “Category Import/Export”">​</a></h3><p><strong>Export:</strong></p><ul><li>Categories -&gt; <strong>Export</strong></li><li>Save JSON file</li></ul><p><strong>Import:</strong></p><ul><li>Categories -&gt; <strong>Import</strong></li><li>Upload JSON file</li><li>Choose override option</li></ul><h2 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to “Keyboard Shortcuts”">​</a></h2><h3 id="edit-poc" tabindex="-1">Edit PoC <a class="header-anchor" href="#edit-poc" aria-label="Permalink to “Edit PoC”">​</a></h3><ul><li>In MonacoEditor you have all the VS Code shortcuts</li><li><code>Ctrl/Cmd + V</code>: Paste image in PoC image type</li><li><code>Alt + R</code>: Add request/response PoC</li><li><code>Alt + I</code>: Add image PoC</li><li><code>Alt + T</code>: Add text PoC</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to “Next Steps”">​</a></h2><ul><li><strong><a href="/kryvea/templating">Templating Guide</a></strong> - Create custom report templates</li><li><strong><a href="./troubleshooting">Troubleshooting</a></strong> - Common issues</li></ul>',38))])}const f=a(n,[["render",g]]);export{b as __pageData,f as default};
